import React, {useState} from 'react';
import Adventures from './Adventures';

/***
 * Displays a grid of current adventures
 */
 function Home() {
    const [productCategory, setProductCategory] = useState('');

    return (
      <div className="Home">
        <h1 className="home__tile">Major Electronic Company</h1> <br/>
        <div className="product-nav">
          <button onClick={() => setProductCategory('')}>All</button>
          <button onClick={() => setProductCategory('appliances')}>Appliances</button>
          <button onClick={() => setProductCategory('mobile')}>Mobile</button>
        </div>
        <Adventures productCategory={productCategory} />
        <h2 className="home__tile">   Contact Us  </h2>   <br/>  <Contact/>
    </div>
    );
}

export default Home;
