import React, { useEffect, useState } from 'react';
import { Link, useNavigate, useParams} from "react-router-dom";
import CurrencyFormat from 'react-currency-format';
import backIcon from '../images/icon-close.svg';
import Error from './Error';
import Loading from './Loading';
import { mapJsonRichText } from '../utils/renderRichText';
import './Footer.scss';
import { getAllFooter } from '../api/persistedQueries';
import LocaleContext from '../LocaleContext';
import { useContext } from "react";

function Footer() {

    // params hook from React router
    const { slug } = useParams();
    const navigate = useNavigate();
    const [response, setResponse] = useState();
    const { locale } = useContext(LocaleContext);
                    console.log("localefooter",locale);
    useEffect(() => {
        // set response to null on change
        setResponse();
        getAllFooter(locale).then(response => setResponse(response));
    }, [slug]);


    //If query response is null then return a loading icon...
    if(!response) return <Loading />;

    //If there is an error with the GraphQL query
    if(response && response.errors) return <Error errorMessage={response.errors} />;


    //Set adventure properties variable based on graphQL response
    const currentProduct = getProduct(response);

    // set references of current adventure
    const references = response.data.footerList._references;

    //Must have title, path, and image
    if( !currentProduct) {
      return <NoProductFound />;
    }

    return (<div className="product-detail">
        <button className="product-detail-close-button" onClick={() => navigate(-1)} >
            <img className="Backbutton-icon" src={backIcon} alt="Return" />
        </button>
        <ProductDetailRender {...currentProduct} references={references}/>
    </div>);
}

function ProductDetailRender({footerTitle}) {

    return (<>
            <div> {mapJsonRichText(footerTitle.json)} </div>
            <hr/> </>
    );
}

function NoProductFound() {
    return (
    <div className="product-detail">
        <Link className="product-detail-close-button" to={"/"}>
            <img className="Backbutton-icon" src={backIcon} alt="Return" />
        </Link>
        <Error errorMessage="Missing data, footer could not be rendered." />
  </div>
  );
}

/**
 * Helper function to get the first adventure from the response
 * @param {*} response 
 */
function getProduct(response) {

    if(response && response.data && response.data.footerList && response.data.footerList.items) {
        // expect there only to be a single adventure in the array
        if(response.data.footerList.items.length === 1) {
            return response.data.footerList.items[0];
        } else {return response.data.footerList.items[0]}
    }
    return undefined;
}
